package io.github.dudupuci.usecases.cliente.atualizar;

import io.github.dudupuci.usecases.base.UnitUseCase;

public abstract class AtualizarClienteUseCase extends UnitUseCase<AtualizarClienteInput> {
}

package io.github.dudupuci.usecases.cliente.atualizar;

import io.github.dudupuci.repositories.ClienteRepository;

public class AtualizarClienteUseCaseImpl extends AtualizarClienteUseCase {

    private final ClienteRepository clienteRepository;

    public AtualizarClienteUseCaseImpl(ClienteRepository clienteRepository) {
        this.clienteRepository = clienteRepository;
    }

    @Override
    public void execute(AtualizarClienteInput atualizarClienteInput) {
        try {
            final var cliente = AtualizarClienteInput.criarEntidade(atualizarClienteInput);

            // Verifica se o cliente existe
            this.clienteRepository.buscarPorId(cliente.getId())
                    .orElseThrow(() -> new RuntimeException("Cliente não encontrado com ID: " + cliente.getId()));

            cliente.validar();
            this.clienteRepository.atualizar(cliente);

        } catch (Exception err) {
            throw new RuntimeException(err);
        }
    }
}
package io.github.dudupuci.usecases.cliente.atualizar;

import io.github.dudupuci.entities.Cliente;
import io.github.dudupuci.enums.Sexo;

public record AtualizarClienteInput(
        Long id,
        String nome,
        String sexo,
        String email,
        String documento,
        String telefone,
        String sobre
) {

    public static Cliente criarEntidade(AtualizarClienteInput input) {
        final var cliente = new Cliente();
        cliente.setId(input.id());
        cliente.setNome(input.nome());
        cliente.setSexo(Sexo.valueOf(input.sexo()));
        cliente.setEmail(input.email());
        cliente.setDocumento(input.documento());
        cliente.setTelefone(input.telefone());
        cliente.setSobre(input.sobre());
        return cliente;
    }

}

